package cls_4;

public class Typecasting {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		byte b=15;
		int x=300;
		System.out.println(b);
		System.out.println(x);
	    b=(byte)(x);
		System.out.println(b);
		byte bb=-5;
		int ii=784647771;
		bb=(byte)(ii);
		System.out.println(bb);
		
		long l=1000000000000l;
		
		float f=7647.61f;
		double d=7647.61;
		
		boolean b1= true;
		boolean b2= false;
		
		char ch='l';
		System.out.println((int)ch);

	}

}
